<?phpclass Campaign extends Controller{    function __construct() {        parent::__construct();    }        function index(){        $this->view->items = $this->model->get_objects();        $this->view->render('campaign/index');    }       public function register(){        $this->view->items = $this->model->get_objects();        $this->view->render('campaign/form/register', true);    }        public function save(){        $this->model->insert_object($_POST);    }    public function bulk_upload(){        $this->model->insert_bulk_upload($_POST);    }    public function edit_item( $id ){        $this->view->items = $this->model->get_objects();        $this->view->item = $this->model->get_object_detail($id);        $this->view->render('campaign/form/edit_type', true);    }    public function update_object(){        $this->model->update_object($_POST);    }    public function delete_item(){        $ids = (is_array($_POST['ids'])) ? $_POST['ids'] : die();        $this->model->delete_object($ids);    }        public function getAccounts()    {        Echo json_encode($this->model->campaignGetAccounts());    }}